Shader.ShaderProgram:

uniform sampler2D tex;
varying vec2 texcoord;

void main()
{
    vec4 baseColor = texture2D(tex, texcoord);

    // Trans flag colors: blue, pink, white, pink, blue
    float y = texcoord.y;
    vec3 overlay;
    if (y < 0.2)       overlay = vec3(91.0/255.0, 206.0/255.0, 250.0/255.0);
    else if (y < 0.4)  overlay = vec3(245.0/255.0, 169.0/255.0, 184.0/255.0);
    else if (y < 0.6)  overlay = vec3(1.0, 1.0, 1.0);
    else if (y < 0.8)  overlay = vec3(245.0/255.0, 169.0/255.0, 184.0/255.0);
    else               overlay = vec3(91.0/255.0, 206.0/255.0, 250.0/255.0);

    vec3 final = mix(baseColor.rgb, overlay, 0.6); // Blend strength
    gl_FragColor = vec4(final, baseColor.a);
}
